function get_AgentBrowser($agent)
{
	if (preg_match('/MSIE\s([^\s|;]+)/i', $agent, $regs)) {
		$outputer = 'IE浏览器';
	} else if (preg_match('/FireFox\/([^\s]+)/i', $agent, $regs)) {
		$outputer = '火狐浏览器';
	} else if (preg_match('/Maxthon([\d]*)\/([^\s]+)/i', $agent, $regs)) {
		$outputer = 'Edge';
	} else if (preg_match('#360([a-zA-Z0-9.]+)#i', $agent, $regs)) {
		$outputer = '360极速';
	} else if (preg_match('/Edge([\d]*)\/([^\s]+)/i', $agent, $regs)) {
		$outputer = 'Edge';
	} else if (preg_match('/UC/i', $agent)) {
		$outputer = 'UC浏览器';
	} else if (preg_match('/QQ/i', $agent, $regs) || preg_match('/QQ Browser\/([^\s]+)/i', $agent, $regs)) {
		$outputer = 'QQ浏览器';
	} else if (preg_match('/UBrowser/i', $agent, $regs)) {
		$outputer = 'UC浏览器';
	}else if (preg_match('/BIDU/i', $agent, $regs)) {
        $outputer = '百度浏览器';
    } else if (preg_match('/Opera[\s|\/]([^\s]+)/i', $agent, $regs)) {
		$outputer = 'Opera';
	} else if (preg_match('/Chrome([\d]*)\/([^\s]+)/i', $agent, $regs)) {
		$outputer = 'Chrome';
	} else if (preg_match('/safari\/([^\s]+)/i', $agent, $regs)) {
		$outputer = 'Safari';
	} else {
		$outputer = 'Chromium';
	}
	return $outputer;
}
function get_AgentOS($agent)
{
	$os = "Linux";
	if (preg_match('/windows/i', $agent)) {
		$os = 'Windows';
	} else if (preg_match('/android/i', $agent)) {
		$os = 'Android';
	} else if (preg_match('/ubuntu/i', $agent)) {
		$os = 'Ubuntu';
	} else if (preg_match('/iPhone/i', $agent)) {
		$os = 'iPhone';
	} else if (preg_match('/mac/i', $agent)) {
		$os = 'MacOS';
	} else {
		$os = 'Linux';
	}
	return $os;
}
/* 时间更替（当年） ok */
function get_lastyear($date){
    $d = new Typecho_Date(Typecho_Date::gmtTime());
    $now = $d->format('Y-m-d H:i:s');
    $t = strtotime($now) - $date;
    
    $time_units = array(
        31536000 => '年',
        2592000 => '月',
        604800 => '周',
        86400 => '天',
        3600 => '小时',
        60 => '分钟',
        1 => '秒'
    );
    
    foreach ($time_units as $unit => $label) {
        if ($t >= $unit) {
            $value = floor($t / $unit);
            return $value . $label . '前';
        }
    }
}
