function time_ago($ptime) {
    $etime = time() - $ptime;
    if ($etime < 1) {
        return '刚刚';
    }
    $interval = array (
        12 * 30 * 24 * 60 * 60 => '年',
        30 * 24 * 60 * 60 => '个月',
        7 * 24 * 60 * 60 => '周',
        24 * 60 * 60 => '天',
        60 * 60 => '小时',
        60 => '分钟',
        1 => '秒'
    );
    foreach ($interval as $seconds => $unit) {
        $duration = floor($etime / $seconds);
        if ($duration >= 1) {
            return $duration . $unit . '前';
        }
    }
}
function darkmode(){
    $darkmode = false;
    if(isset($_COOKIE['night'])){
        $darkmode = ($_COOKIE['night'] == 1) ? true : false;
    }elseif(Helper::options()->cat_darkmode && Helper::options()->cat_darkmode != 'auto'){
        $darkmode = (Helper::options()->cat_darkmode == 'dark') ? true : false;
    }else{
        $darkmode = (date("H") < 6 || date("H") >= 18) ? true : false;
    }
    return $darkmode;
}

function cat_check_XSS($text)
{
    $isXss = false;
    $list = array(
        '/onabort/is',
        '/onblur/is',
        '/onchange/is',
        '/onclick/is',
        '/ondblclick/is',
        '/onerror/is',
        '/onfocus/is',
        '/onkeydown/is',
        '/onkeypress/is',
        '/onkeyup/is',
        '/onload/is',
        '/onmousedown/is',
        '/onmousemove/is',
        '/onmouseout/is',
        '/onmouseover/is',
        '/onmouseup/is',
        '/onreset/is',
        '/onresize/is',
        '/onselect/is',
        '/onsubmit/is',
        '/onunload/is',
        '/eval/is',
        '/ascript:/is',
        '/style=/is',
        '/width=/is',
        '/width:/is',
        '/height=/is',
        '/height:/is',
        '/src=/is',  
    );
    if (strip_tags($text)) {
        for ($i = 0; $i < count($list); $i++) {
            if (preg_match($list[$i], $text) > 0) {
                $isXss = true;
                break;
            }
        }
    } else {
        $isXss = true;
    };
    return $isXss;
}

function cat_reply($text,$word = true)
{
    if (cat_check_XSS($text)) {
        $text = "该回复疑似异常，已被系统拦截！";
    }
    if($word == true){
        $text = strip_tags($text, "<img>");
    }else{
        $text = rtrim(strip_tags($text), "\n");
    }
    return $text;
}

function get_comment_at($coid)
{
    $db   = Typecho_Db::get();
    $prow = $db->fetchRow($db->select('parent,status')->from('table.comments')
        ->where('coid = ?', $coid));
    $mail = "";
    $parent = $prow['parent'];
    if ($parent != "0") {
        $arow = $db->fetchRow($db->select('author,status,mail')->from('table.comments')
            ->where('coid = ?', $parent));
        $author = (!empty($arow) && isset($arow['author']))?$arow['author']:'';
        $mail = (!empty($arow) && isset($arow['mail']))?$arow['mail']:'';
        if($author && $arow['status'] == "approved"){
            echo '<a style="font-size: 0.75rem;display: inline; color: var(--colorC);" href="#comment-' . $parent . '">@' . $author . '</a>';
        }
    }
}

